import React, { Component } from 'react';
import { connect } from "react-redux";
import * as actions from "../../store/actions";

class SelectSlot extends Component {
  constructor(props) {
    super(props);
    this.state = {
      slotId: null
    };
    this.handlerNextBtnClick.bind(this);
  }

  componentDidMount() {
    let {phyId, clinicId, appointmentDate} = this.props.appointmentData;
    this.props.onGetSlots(phyId, clinicId, appointmentDate);
  }

  handlerNextBtnClick = () => {
    this.state.appointmentData.slotId = this.state.slotId;
    this.props.onSetAppointmentData(this.state.appointmentData);
  };

  render() {
    return (
      'Slots available'
    );
  }
}

const mapStateToProps = (state) => {
  return {
    userProfile: state.UserProfile.userProfile,
    profileCompliant: state.UserProfile.userProfile.dateofbirth !== '0000-00-00',
    appointmentData: state.newAppointment.appointmentData,
    slots: state.selectSlot.slots
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onGetSlots: (phyId, clinicId, slotDate) => dispatch(actions.getSlots(phyId, clinicId, slotDate)),
    onSetAppointmentData: (appointmentData) => dispatch(actions.setAppointmentData(appointmentData))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(SelectSlot);
